using NUnit.Framework;
using AsmodatStandard.Extensions;
using AsmodatStandard.Extensions.Collections;
using AsmodatStandard.Extensions.Imaging;
using AsmodatStandard.Extensions.ImageSharp;
using System.Drawing.Imaging;
using System.Drawing;

namespace AsmodatStandardTest.Extensions.Imaging.StringExTests
{
    [TestFixture]
    public class StringExTest
    {
        [Test]
        public void DataImageToBitmap()
        {
            string data = @"data:image/png;base64,";
            var png = data.DataImageToImage();

            Assert.AreEqual(png.GetImageFormat(), ImageFormat.Png);

            var gif = png.Convert(ImageFormat.Gif, quality: 100);
            Assert.AreEqual(gif.GetImageFormat(), ImageFormat.Gif);

            var gifArr = gif.ToByteArray();
            var gifString = $"data:image/gif;base64,{gifArr.ToBase64String()}";
            var gifDecoded = gifString.DataImageToImage();

            Assert.AreEqual(gifDecoded.GetImageFormat(), ImageFormat.Gif);

            var gifArr2 = gifDecoded.ToByteArray();
            Assert.AreEqual(gifArr2.ToHexString(), gifArr.ToHexString());
        }

        [Test]
        public void DataImageToBitmapImageSharp()
        {
            string data = @"data:image/png;base64,";
            var png = data.DataImageToBitmapWithImageSharp();

            Assert.AreEqual(png.GetImageFormat(), ImageFormat.Png);

            var gif = png.ImageSharpConvert(ImageFormat.Gif, quality: 100);
            Assert.AreEqual(gif.GetImageFormat(), ImageFormat.Gif);

            var gifArr = gif.ToByteArray();
            var gifString = $"data:image/gif;base64,{gifArr.ToBase64String()}";
            var gifDecoded = gifString.DataImageToImageWithImageSharp();

            Assert.AreEqual(gifDecoded.GetImageFormat(), ImageFormat.Gif);

            var gifArr2 = gifDecoded.ToByteArray();
            //Assert.AreEqual(gifArr2.ToHexString(), gifArr.ToHexString());
        }
    }
}
